import { styled, type HTMLStyledProps } from "@styled-system/jsx"
import { button } from "@styled-system/recipes"
import * as React from "react"
import { Slot } from "@radix-ui/react-slot"

export type BaseButtonProps = React.ComponentPropsWithoutRef<"button"> & {
  asChild?: boolean
}

export const BaseButton = React.forwardRef<HTMLButtonElement, BaseButtonProps>(
  ({ asChild, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"

    return <Comp role="button" ref={ref} {...props}></Comp>
  },
)

BaseButton.displayName = ""

export const Button = styled(BaseButton, button)
export type ButtonProps = HTMLStyledProps<typeof Button>

import { defineSafe } from "@utils/defineSafe"

export const buttonRecipe = defineSafe.recipe({
  className: "button",
  description: "Styles for the Button component",
  base: {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    rounded: "md",
    textStyle: "sm",
    fontWeight: "medium",
    transition: "colors",
    cursor: "pointer",
    gap: "2",
    _focusVisible: {
      outlineColor: "background",
      outlineWidth: "2px",
      ringColor: "ring",
      ringOffset: "2",
    },

    _disabled: {
      cursor: "not-allowed",
      opacity: "50%",
    },
  },
  variants: {
    variant: {
      default: {
        bg: "primary",
        color: "primary.foreground",

        _hover: {
          bg: "primary/90",
        },
      },
      destructive: {
        bg: "destructive",
        color: "destructive.foreground",

        _hover: {
          bg: "destructive/90",
        },
      },
      outline: {
        border: "input",
        bg: "background",

        _hover: {
          bg: "accent",
          color: "accent.foreground",
        },
      },
      secondary: {
        bg: "secondary",
        color: "secondary.foreground",

        _hover: {
          bga: "secondary/90",
        },
      },
      ghost: {
        _hover: {
          bg: "accent",
          color: "accent.foreground",
        },
      },
      link: {
        color: "primary",
        textUnderlineOffset: "4px",

        _hover: {
          textDecoration: "underline",
        },
      },
    },
    size: {
      default: {
        h: "10",
        px: "4",
        py: "2",
      },
      sm: {
        h: "9",
        rounded: "md",
        px: "3",
      },
      lg: {
        h: "11",
        rounded: "md",
        px: "8",
      },
      icon: {
        h: "10",
        w: "10",
      },
    },
  },
  defaultVariants: {
    variant: "default",
    size: "default",
  },
})
